var Promise = require('bluebird');
var createQuery = require('./create_query.js');
var validateMarvelLicense = require('./validate_marvel_license');
module.exports = function getClusters(req, indices) {
  var callWithRequest = req.server.plugins.elasticsearch.callWithRequest;
  var config = req.server.config();
  // Get the params from the POST body for the request
  var start = req.payload.timeRange.min;
  var end = req.payload.timeRange.max;

  var params = {
    index: indices,
    type: 'cluster_stats',
    meta: 'get_clusters_stats',
    ignore: [404],
    // terms agg for the cluster_uuids
    body: {
      size: 0, // return no hits, just aggregation buckets
      query: createQuery({
        start: start,
        end: end,
        clusterUuid: null
      }),
      aggs: {
        cluster_uuids: {
          terms: {
            field: 'cluster_uuid'
          }
        }
      }
    }
  };
  return callWithRequest(req, 'search', params).then(function (statsResp) {
    var statsBuckets = _.get(statsResp, 'aggregations.cluster_uuids.buckets');
    if (_.isArray(statsBuckets)) {

      return Promise.map(statsBuckets, function (uuidBucket) {
        var cluster = {
          cluster_uuid: uuidBucket.key
        };

        var infoParams = {
          index: config.get('marvel.index'),
          type: 'cluster_info',
          meta: 'get_clusters_info',
          id: cluster.cluster_uuid
        };

        return callWithRequest(req, 'get', infoParams).then(function (infoResp) {
          var infoDoc = infoResp._source;

          cluster.cluster_name = infoDoc.cluster_name;
          var license = infoDoc.license;
          if (license && validateMarvelLicense(cluster.cluster_uuid, license)) {
            cluster.license = license;
            cluster.version = infoDoc.version;
          }

          return cluster;
        });
      })
      // Only return clusters with valid licenses
      .filter(function (cluster) {
        return cluster.license;
      });
    }
  });
};
