var _ = require('lodash');
var moment = require('moment');
module.exports = function (options) {
  options = _.defaults(options, { filters: [] });
  var clusterFilter;
  if (options.clusterUuid) {
    clusterFilter = { term: { cluster_uuid: options.clusterUuid } };
  }
  var timeRangeFilter = {
    range: {
      timestamp: {
        format: 'epoch_millis'
      }
    }
  };
