  }
  if (options.end) {
    timeRangeFilter.range.timestamp.lte = moment.utc(options.end).valueOf();
  }
  const filters = [clusterFilter].concat(options.filters);
  if (options.end || options.start) {
    filters.push(timeRangeFilter);
  }
  return {
    filtered: {
      filter: {
        bool: {
          must: filters
        }
      }
    }
  };
};

