  [ d(7.5, 'second'), d(5, 'second') ],
  [ d(15, 'second'), d(10, 'second') ],
  [ d(45, 'second'), d(30, 'second') ],
  [ d(3, 'minute'), d(1, 'minute') ],
  [ d(9, 'minute'), d(5, 'minute') ],
